<?phpinclude_once ('Controller.php');//базовый контроллер сайтаabstract class C_Base extends Controller{	protected $title; 	//заголовок страницы 	protected $content;	//содержание страницы		//конструктор	function __construct()	{			}	//виртуальный обработчик запроса	protected function OnInput()	{		$this->title = 'Парсинг';		$this->content = '';	}	//виртуальный генератор html	protected function OnOutput()	{	$vars = array('title' => $this->title, 'content' => $this->content);	$page = $this->Template('v/v_main.php', $vars); 	echo $page;	}}		 