<?phpinclude_once ('c_base.php');class C_All extends C_Base{	public $articles;	public $title;	public $content;	private $all;		function __construct()	{		$mArticles = new M_Articles();		// вот здесь ты выхываешь метод articles_all у объекта класса M_Articles. Всё правильно.		$this->articles = $mArticles->articles_all();	}		protected function OnInput()	{				parent::OnInput();		if ($this->IsPost())		{			if(isset($_POST['articles']))			{			$this->article = $_POST['article'];			$allart = $mArticles->articles_all();			}		foreach($articles as $key => $article)		$articles[$key]['intro']  = $mArticles->articles_intro($article);				}	}		protected function OnOutput()	{		$vars = array('articles' => $this->articles);		$this->content = $this->Template('v/v_all.php', $vars);		parent::OnOutput();			}}