<?phpinclude_once('c_base.php');class C_Editor extends C_Base{	public $articles;		function __construct()	{			}		protected function OnInput()	{		parent::OnInput();		$mArticles = new M_Articles();		$this->articles = $mArticles->articles_all();	}		protected function OnOutput()	{		$vars = array('articles' => $this->articles);				$this->content = $this-> Template('v/v_editor.php', $vars); 		parent::OnOutput();	}}