<?phpinclude_once('c_base.php');class C_New extends C_Base{	public $title;	public $content;		function __construct()	{			}		protected function OnInput()	{		parent::OnInput();		$mArticles = new M_Articles();		if (!empty($_POST)) //если не пуста		{				if ($mArticles->articles_new($_POST['title'], $_POST['content']))			{				header('Location: index.php');				die();			}		$this->title = $_POST['title'];		$this->content = $_POST['content'];			}		else		{			$this->title = '';			$this->content = '';		}	}		protected function OnOutput()	{		$vars = array('title' => $this->title, 'content' => $this->content);		$this->content = $this->Template('v/v_new.php', $vars);		parent::OnOutput();	}}